"use client"
import { useState, useEffect } from "react";
import CategoryBar from "@/components/CategorySection";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import Link from "next/link";
import PromoBanner from "@/components/PromoBanner";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/swiper-bundle.css";
import productsData from "../../../../products";

const ProductPage = () => {
  const [filters, setFilters] = useState({
    availability: "all",
    price: "all",
    productType: "all",
    brand: "all",
  });

  const [products, setProducts] = useState([]);
  const [isClient, setIsClient] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setIsClient(true);

    if (typeof window !== "undefined") {
      const storedProducts = productsData;

      if (storedProducts) {
        const makeUpCityProducts = (productsData.MakeUpCity || []).flatMap(
          (item) => item.products || []
        );

        const allProducts = [
          ...(productsData.Naheed || []),
          ...makeUpCityProducts,
          ...(productsData.TheSkinFit || []),
          ...(productsData.highfy || []),
        ];

        // Extract brand and type from product names
        const processedProducts = allProducts.map((product) => {
          let brand = "Unknown";
          let type = "Unknown";
        
          if (product.name && product.name.toLowerCase().includes("loreal")) brand = "L'Oreal";
          if (product.name && product.name.toLowerCase().includes("ponds")) brand = "Pond's";
          if (product.name && product.name.toLowerCase().includes("garnier")) brand = "Garnier";
          if (product.name && product.name.toLowerCase().includes("saeed ghani")) brand = "Saeed Ghani";
          if (product.name && product.name.toLowerCase().includes("faiza beauty cream")) brand = "Faiza Beauty Cream";
          if (product.name && product.name.toLowerCase().includes("vaseline")) brand = "Vaseline";
        
          if (product.name && product.name.toLowerCase().includes("serum")) type = "serum";
          if (product.name && product.name.toLowerCase().includes("moisturizer")) type = "moisturizer";
        
          return { ...product, brand, type };
        });
        
        setProducts(processedProducts);
        setIsLoading(false);
      }
    }
  }, []);

  if (!isClient || isLoading) {
    return (
      <div className="flex w-full min-h-screen items-center justify-center bg-customGreen p-4">
        <div className="relative flex items-center justify-center">
          <div className="spinner w-24 h-24 border-8 border-gray-200 border-t-green-600 rounded-full animate-spin"></div>
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  }

  // Filter products based on selected filters
  const filteredProducts = products.filter((product) => {
    const numericPrice = product.price 
      ? parseInt(product.price.replace(/[^0-9]/g, ""), 10) 
      : 0;
    
    const matchesAvailability =
      filters.availability === "all" ||
      (filters.availability === "in-stock" && product.inStock) ||
      (filters.availability === "out-of-stock" && !product.inStock);

    const matchesPrice =
      filters.price === "all" ||
      (filters.price === "low" && numericPrice < 1000) ||
      (filters.price === "mid" && numericPrice >= 1000 && numericPrice <= 2000) ||
      (filters.price === "high" && numericPrice > 2000);

    const matchesProductType =
      filters.productType === "all" ||
      (product.type && product.type.toLowerCase() === filters.productType.toLowerCase());

    const matchesBrand =
      filters.brand === "all" ||
      (product.brand &&
        product.brand.toLowerCase().replace(/[^a-z0-9]/gi, "") === filters.brand.toLowerCase().replace(/[^a-z0-9]/gi, ""));

    return matchesAvailability && matchesPrice && matchesProductType && matchesBrand;
  });

  const handleFilterChange = (filterType, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [filterType]: value,
    }));
  };

  // Handle brand logo click
  const handleBrandClick = (brand) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      brand,
    }));
  };

  return (
    <>
      <PromoBanner />
      <Navbar />
      <CategoryBar />

      <div className="py-4 bg-gray-100">
        <h2 className="text-lg font-bold text-center mb-4">Top Skincare Brands</h2>
        <div className="relative">
          <div className="slider-container flex overflow-x-hidden space-x-4">
            {[{ name: "L'Oreal", logo: "data:image/png;base64," }, 
              { name: "Pond's", logo: "data:image/jpeg;base64," }, 
              { name: "Garnier", logo: "data:image/jpeg;base64," },
              { name: "Saeed Ghani", logo: "data:image/png;base64," },
              { name: "Faiza Beauty Cream", logo: "data:image/jpeg;base64," },
              { name: "Vaseline", logo: "data:image/jpeg;base64," }].map((brand, index) => (
              <div key={index} className="flex-shrink-0 w-auto">
                <div className="flex flex-col items-center">
                  <img
                    src={brand.logo}
                    alt={brand.name}
                    className="w-44 h-44 object-contain rounded-full border p-1 shadow cursor-pointer"
                    onClick={() => handleBrandClick(brand.name.toLowerCase())} // Call the handleBrandClick function on logo click
                  />
                  <p className="text-sm font-medium mt-2 text-center">{brand.name}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="min-h-screen bg-white">
        <div className="flex flex-col md:flex-row mt-4 px-4">
          <aside className="w-full md:w-1/4 p-4 bg-white rounded-lg mb-4 md:mb-0">
            <h2 className="font-bold text-lg mb-4">Filter:</h2>
            <div className="space-y-4">
              {/* Price Filter */}
              <div>
                <h3 className="font-semibold mb-2">Price</h3>
                <select
                  className="w-full p-2 border rounded"
                  value={filters.price}
                  onChange={(e) => handleFilterChange("price", e.target.value)}
                >
                  <option value="all">All</option>
                  <option value="low">Under Rs. 1000</option>
                  <option value="mid">Rs. 1000 - Rs. 2000</option>
                  <option value="high">Above Rs. 2000</option>
                </select>
              </div>

              {/* Product Type Filter */}
              <div>
                <h3 className="font-semibold mb-2">Product Type</h3>
                <select
                  className="w-full p-2 border rounded"
                  value={filters.productType}
                  onChange={(e) => handleFilterChange("productType", e.target.value)}
                >
                  <option value="all">All</option>
                  <option value="serum">Face Serum</option>
                  <option value="moisturizer">Moisturizer</option>
                </select>
              </div>

              {/* Brand Filter */}
              <div>
                <h3 className="font-semibold mb-2">Brand</h3>
                <select
                  className="w-full p-2 border rounded"
                  value={filters.brand}
                  onChange={(e) => handleFilterChange("brand", e.target.value)}
                >
                  <option value="all">All</option>
                  <option value="loreal">L'Oreal</option>
                  <option value="ponds">Pond's</option>
                  <option value="garnier">Garnier</option>
                  <option value="saeed ghani">Saeed Ghani</option>
                  <option value="faiza beauty cream">Faiza Beauty Cream</option>
                  <option value="vaseline">Vaseline</option>
                </select>
              </div>
            </div>
          </aside>

          {/* Product Grid */}
          <main className="w-full max-w-6xl mx-auto grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6 px-4">
            {filteredProducts.length > 0 ? (
              filteredProducts.map((product) => (
                <Link href={`/product/${product.name}`} key={product._id}>
                  <div className="border rounded-lg shadow-lg p-4 bg-white hover:shadow-xl transition hover:scale-105 flex flex-col h-full">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="w-full h-40 object-cover rounded-md"
                    />
                    <h3 className="text-base font-semibold mt-2 flex-grow">{product.name}</h3>
                    <p className="text-gray-500">Rs. {product.price}</p>
                    <button className="mt-auto w-full bg-customGreen text-white py-2 rounded-lg hover:bg-green-700">
                      View Detail
                    </button>
                  </div>
                </Link>
              ))
            ) : (
              <p>No products available.</p>
            )}
          </main>
        </div>

        <div className="mt-2">
          <Footer />
        </div>
      </div>
    </>
  );
};

export default ProductPage;
